script:
// Server Script for POS Profile
// Script Type: DocType
// DocType: POS Profile
// Event: Before Save, Before Load

frappe.ui.form.on('POS Profile', {
    refresh: function(frm) {
        // Set the max_discount_percentage_allowed field as readonly 
        // when allow_user_to_edit_additional_discount is not checked
        set_discount_field_readonly(frm);
    },
    
    allow_user_to_edit_additional_discount: function(frm) {
        // Update readonly state when checkbox is toggled
        set_discount_field_readonly(frm);
    }
});

function set_discount_field_readonly(frm) {
    // Check if the user is allowed to edit additional discount
    const is_discount_allowed = frm.doc.allow_user_to_edit_additional_discount || 0;
    
    // Set readonly property for max_discount_percentage_allowed
    frm.set_df_property('max_discount_percentage_allowed', 'read_only', !is_discount_allowed);
    
    // Optional: You can also hide the field completely instead of making it readonly
    // frm.toggle_display('max_discount_percentage_allowed', is_discount_allowed);
    
    // If making readonly and the field is now readonly, you might want to clear the value
    if (!is_discount_allowed && frm.doc.max_discount_percentage_allowed) {
        frm.set_value('max_discount_percentage_allowed', 0);
        frm.refresh_field('max_discount_percentage_allowed');
    }
}

frappe.ui.form.on("POS Profile", {
    // Trigger when the form is loaded
    refresh: function(frm) {
        // Call the function to handle the checkbox and display mode field
        handle_custom_display_mode(frm);
    },

    // Trigger when the custom_enable_compact_mode_option checkbox is toggled
    custom_enable_compact_mode_option: function(frm) {
        // Call the function to handle the checkbox and display mode field
        handle_custom_display_mode(frm);
    }
});

// Function to handle the custom_display_mode field behavior
function handle_custom_display_mode(frm) {
    // Check if the checkbox is unchecked
    if (!frm.doc.custom_enable_compact_mode_option) {
        // Set custom_display_mode to "Full Mode"
        frm.set_value("custom_display_mode", "Full Mode");

        // Disable and hide the custom_display_mode field
        frm.set_df_property("custom_display_mode", "read_only", 1);
        frm.set_df_property("custom_display_mode", "hidden", 1);
    } else {
        // Enable and show the custom_display_mode field
        frm.set_df_property("custom_display_mode", "read_only", 0);
        frm.set_df_property("custom_display_mode", "hidden", 0);
    }
}
